        /// <summary>
        /// Player Login Main
        /// </summary>
        /// <param name="version">Client Version</param>
        public void SendMainPacket(string version)
        {
            Response.Clear();
            Response.Write(new byte[] { 0x44, 0x00 });
            Response.WriteByte(0);
            Response.WritePStr(version);
            Response.Write(GetLoginInfo());
            Response.Write(Statistic());
            Response.Write(Inventory.GetTrophyInfo());
            Response.Write(Inventory.GetEquipData());
            Response.Write(GetMapStatistic());
            Response.Write(Inventory.GetEquipInfo());
            Response.Write(Tools.GameTime());
            Response.WriteByte(0);
            Response.WriteUInt16(2);
            Response.Write(new byte[]
            {
                0xFF, 0xFF, 0xFF,
                0xFF, 0xFF, 0xFF
            });
            Response.WriteUInt32(0);
            if (IDState > 0)
            {
                Response.WriteByte(0);
                Response.Write(IDState);
                Response.WriteZero(3);
            }
            else
            {
                Response.Write(Server.Data.BlockFunc);//block? 37363656
            }
            Response.WriteUInt32(2);// block 2? 363, 9?
            Response.WriteUInt32(Server.Data.Property); // Grand Prix 2048, normal 0, 256 ?
            Response.Write(GetGuildInfo());
            SendResponse();
        }

        /// <summary>
        /// Is Test Achivement
        /// </summary>
        /// <param name="TypeID"></param>
        /// <param name="Count"></param>
        public void AddAchivementQuest(uint TypeID, uint Count)
        {
            Achievements.Add(new TAchievement()
            {
                ID = 1,
                TypeID = TypeID,
                AchievementType = 1
            });
            AchievemetCounters.Add(1, new TAchievementCounter() { ID = 1, TypeID = TypeID, Quantity = 1 });

            var Quest = new TAchievementQuest
            {
                ID = 1,
                AchievementIndex = 1,
                AchievementTypeID = TypeID,
                CounterIndex = 1,
                SuccessDate = (uint)Tools.UnixTimeConvert(DateTime.Now),
                Total = 1
            };

            this.AchievementQuests.Add(Quest);
        }

        public void SendAchievementQuest()
        {
            Response.Write(new byte[] { 0x1D, 0x02 });
            Response.Write(0);
            Response.WriteInt32(AchievemetCounters.Count);
            foreach (var Counter in AchievemetCounters.Values)
            {
                Response.WriteByte(1);
                Response.WriteUInt32(Counter.TypeID);
                Response.WriteUInt32(Counter.ID);
                Response.WriteUInt32(Counter.Quantity);
            }
            SendResponse();
        }

        public void SendAchievement()
        {
            UInt32 Count = 0;
            UInt32 CounterTypeID, CounterID;
            var Packet = new PangyaBinaryWriter();
            var Packet2 = new PangyaBinaryWriter();
            Packet.Write(new byte[] { 0x1E, 0x02 });
            Packet.Write(0);
            Packet.WriteInt32(AchievemetCounters.Count);
            Packet.WriteInt32(AchievemetCounters.Count);
            foreach (var Achievement in this.Achievements)
            {
                Packet.Write((byte)1);
                Packet.WriteUInt32(Achievement.TypeID);
                Packet.WriteUInt32(Achievement.ID);
                Packet.WriteUInt32(Achievement.AchievementType);
                foreach (var Quest in this.AchievementQuests)
                {
                    if (Quest.AchievementIndex == Achievement.ID)
                    {
                        Count += 1;

                        if (AchievemetCounters.TryGetValue(Quest.CounterIndex, out TAchievementCounter Counter))
                        {
                            CounterTypeID = Counter.TypeID;
                            CounterID = Counter.ID;
                        }
                        else
                        {
                            CounterTypeID = 0;
                            CounterID = 0;
                        }
                        Packet2.WriteUInt32(Quest.AchievementTypeID);
                        Packet2.WriteUInt32(CounterTypeID);
                        Packet2.WriteUInt32(CounterID);
                        Packet2.WriteUInt32(Quest.SuccessDate);
                    }
                }
                Packet.WriteUInt32(Count);
                Packet.Write(Packet2.GetBytes());
            }
            SendResponse(Packet.GetBytes());
        }

